import { AsyncAPI } from "./types";

/**
 *
 * 流量归因参数查询
 */
export const getAnalysisInfo: AsyncAPI<
  {},
  {
    /** 场景类型 */
    type: number;
    /** 主播/作者的抖音号 */
    uniqueId: string;
    /** roomId：直播间id */
    roomId: string;
    /** anchorId：主播 openId */
    anchorId: string;
    /** postId：作者 openId */
    postId: string;
    /** itemId：加密后的视频 id */
    itemId: string;
    /** 群id */
    conversationId: string;
    /** 发私信方的抖音号/群主的抖音号 */
    awemeId: string;
    /** 发私信方/群主在小程序内的openId */
    fromOpenId: string;
    /** 消息id */
    messageId: string;
  }
>;
