import { AsyncAPI } from "./types";

interface AuthSetting {
  /**
   * 是否授权用户信息
   *
   * 对应接口 tt.getUserInfo
   */
  "scope.userInfo"?: boolean;

  /**
   * 是否授权地理位置
   *
   * 对应接口 tt.getLocation
   */
  "scope.userLocation"?: boolean;

  /**
   * 是否授权通讯地址
   *
   * 对应接口 tt.chooseAddress
   */
  "scope.address"?: boolean;

  /**
   * 是否授权录音功能
   *
   * 对应接口: tt.getRecorderManager.start
   */
  "scope.record"?: boolean;

  /**
   * 是否授权保存到相册
   *
   * - tt.saveImageToPhotosAlbum
   * - tt.saveVideoToPhotosAlbum
   */
  "scope.album"?: boolean;

  /** 是否授权摄像头
   *
   * 对应接口:
   * - tt.scanCode
   * - tt.chooseImage
   * - tt.chooseVideo
   */
  "scope.camera"?: boolean;
}

/**
 * ### 获取用户已经授权过的配置
 */
export const getSetting: AsyncAPI<
  {
    /** 是否同时获取用户订阅消息的订阅状态，默认不获取 */
    withSubscriptions?: boolean;
  },
  {
    /** 授权结果 */
    authSetting: AuthSetting;
    /** 订阅消息设置 */
    subscriptionsSetting?: {
      mainSwitch: boolean;
      itemSettings: Record<string, "accept" | "reject">;
      templateSettings: Record<
        string,
        {
          /** 订阅结果, 取值见下表
           * | 值    | 说明                           |
           * | :---- | :----                         |
           * | accept| 用户同意订阅该条 id 对应的模板消息 |
           * | reject| 用户拒绝订阅该条 id 对应的模板消息 |
           * | ban   | 表示已被后台封禁                 |
           * | fail  | 表示该条 id 对应的模版消息授权失败 |
           * | repeat | 表示该条id对应的模板消息重复订阅  |
           * | limit  | 表示用户对该条id对应的模板订阅次数达到上限 |
           */
          status: "accept" | "reject" | "fail" | "repeat" | "limit";
          /** 是否为长期订阅状态(长期订阅状态的模板后续不会弹窗询问用户) */
          alwaysSubscribe: boolean;
          /** 返回模板支持的提醒方式对应的用户授权结果 */
          allowReminderWay: {
            /** 提醒方式, 取值见下表
             * | 值    | 说明                           |
             * | :---- | :----                         |
             * | message_notice| 消息通知。默认支持 |
             * | feed| 推荐频道提醒。开发者需要申请支持信息流通知的模板 |
             * | push| 实时提醒。开发者需要申请支持实时提醒的模板 |
             */
            reminderType: "message_notice" | "feed" | "push";
            /** 授权结果, 取值见下表
             * | 值    | 说明                           |
             * | :---- | :----                         |
             * | reject| 拒绝 |
             * | accept| 同意 |
             */
            reminderStatus: "reject" | "accept";
          }[];
        }
      >;
    };
  }
>;

